        <span class="token keyword">return</span> hoursRound <span class="token operator">+</span> <span class="token string">':'</span> <span class="token operator">+</span> minutesRound <span class="token operator">+</span> <span class="token string">':'</span> <span class="token operator">+</span> secondRound<span class="token punctuation">;</span>
    <span class="token punctuation">}</span><span class="token punctuation">;</span>


    <span class="token doc-comment comment">/**
