    <span class="token keyword">export</span> <span class="token keyword">const</span> mapTree <span class="token operator">=</span> <span class="token punctuation">(</span>arr<span class="token punctuation">,</span> title <span class="token operator">=</span> <span class="token string">'name'</span><span class="token punctuation">,</span> value <span class="token operator">=</span> <span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span>
