            newVal <span class="token operator">=</span> <span class="token punctuation">{</span> <span class="token punctuation">[</span>item<span class="token punctuation">]</span><span class="token operator">:</span> arr<span class="token punctuation">[</span>item<span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token operator">...</span>newVal <span class="token punctuation">}</span><span class="token punctuation">;</span>
        <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
        <span class="token keyword">return</span> <span class="token punctuation">{</span>
            <span class="token literal-property property">title</span><span class="token operator">:</span> arr<span class="token punctuation">[</span>title<span class="token punctuation">]</span><span class="token punctuation">,</span>
            <span class="token literal-property property">value</span><span class="token operator">:</span> <span class="token constant">JSON</span><span class="token punctuation">.</span><span class="token function">stringify</span><span class="token punctuation">(</span>newVal<span class="token punctuation">)</span><span class="token punctuation">,</span>
